require 'base64'
require 'net/http'
require 'net/https'
require 'json'

s = ""


http = Net::HTTP.new('sandbox.itunes.apple.com', 443)
http.use_ssl = true
path = '/verifyReceipt'
data = {'receipt-data' => s}.to_json
headers = {'Content-Type'=> 'application/json'}
resp, data = http.post(path, data, headers)
puts resp.body